document.addEventListener("DOMContentLoaded",()=>{var e=window.getComputedStyle(document.querySelector(".header")).height.replace(/\D/gi,"");document.querySelector(".main-page").style.paddingTop=e+"px";const t=document.querySelector(".main-page__subtitle"),l=document.querySelector(".main-page__subtitle-list"),n=document.querySelectorAll(".main-page__subtitle-list div"),i=window.getComputedStyle(document.querySelector(".main-page__subtitle-list div")).height;t.style.height=i,l.style.height=100*n.length+"%";let o=0;setInterval(()=>{o==+i.slice(0,i.length-2)*(n.length-1)?o=0:o+=+i.slice(0,i.length-2),l.style.transform=`translateY(-${o}px)`},1e3)});